import React from "react";
import { Component } from "react";
/**
 * This comonent is expected to take the city name from http route and tto 
 * list all iternararies for this city
 */
class Itinararies extends Component {
  constructor(props) {
    super(props)
    this.state = {"itinararies": [] }
  }

  render() {
    return ( this.state.itinararies[0].map(i=> <div>{i}</div>) );
  }

  componentDidMount() {
    fetch( "http://localhost:5000/api/tinerary/search?city=Barcelona" )
      .then(response =>response.json())
      .then(data => {this.setState({"itinararies": data[0]}); console.log(data)});
  }

} /* end class itinararies */

export default Itinararies;
/* last line */
